function getToolkitSetting(settingName) {
  return new Promise(function (resolve, reject) {
    try {
      localStorage.getItem(settingName);
      resolve();
    } catch (e) {
      reject(e);
    }
  });
}

function setToolkitSetting(settingName, data) {
  return new Promise(function (resolve, reject) {
    try {
      localStorage.setItem(settingName, data);
      resolve();
    } catch (e) {
      reject(e);
    }
  });
}

function deleteAllToolkitSettings() {
  return new Promise(function (resolve, reject) {
    try {
      localStorage.clear();
      resolve();
    } catch (e) {
      reject(e);
    }
  });
}

function getToolkitSettingKeys() {
  return new Promise(function (resolve, reject) {
    try {
      const keys =  Object(localStorage).keys;
      resolve(keys);
    } catch (e) {
      reject(e);
    }
  });
}

